  #left-sidebar {
    position: fixed;
    max-height: 100%;
    height: 100%;
    background: #212121;
    color: #eee;
    width: 240px;
    transform: translateX(-240px);
    transform-style: preserve-3d;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-delay: 0.1s;
    transition-duration: 0.3s;
    z-index: 22;
    top: 0;
    left: 0;
    margin: 0;
  }
  
  #left-sidebar:after {
    content: '';
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M0 0h24v24H0z' fill='none'/><path d='M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z'/></svg>") center center no-repeat !important;
    background-size: 24px;
    position: absolute;
    display: block;
    height: 48px;
    overflow: hidden;
    top: 8px;
    left: calc(100% + 12px);
    width: 48px;
    z-index: 2;
    cursor: pointer;
    visibility: visible;
    
    transition-delay: .35s;
    transition-duration: .1s;
  }
  #left-sidebar:hover {
    transform: translateX(0px);
    box-shadow: 0 0 15px rgba(0, 0, 0, .3);
  }
  #left-sidebar:hover:after {
    opacity: 0;
    visibility: hidden;
    transform: translateX(-240px);
  }
  
  #left-sidebar h2 {
    font-size: 20px;
  }
  
  #left-sidebar ul li {
    padding: 8px 0;
  }
  
  footer#related{
