  }
  
  footer:after {
    position: fixed;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0);
    content: '';
    top: 0;
    left: 0;
    z-index: 20;
    visibility: hidden;
    
    -moz-transition: all .3s ease-in-out;
    -webkit-transition: all .3s ease-in-out;
    transition: all .3s ease-in-out;
  }
  #left-sidebar:hover ~ footer:after {
    background: rgba(0, 0, 0, .6);
    visibility: visible;
  }
  
  footer {
    border-top: 1px solid #333;
